package com.example.kpmall.DatabaseFiles;

import android.content.Context;
import android.os.AsyncTask;

import androidx.annotation.NonNull;
import androidx.room.Dao;
import androidx.room.Database;
import androidx.room.Room;
import androidx.room.RoomDatabase;
import androidx.sqlite.db.SupportSQLiteDatabase;

import com.example.kpmall.GroceryItem;

import java.util.ArrayList;

@Database(entities = {GroceryItem.class,CartItems.class},version = 1)
public abstract class ShopDatabase extends RoomDatabase {

    public abstract GroceryItemDao groceryItemDao();
    public abstract CartItemDao cartItemDao();

    private static ShopDatabase instance;

    public static synchronized ShopDatabase getInstance(Context context){
        if (instance == null) {
            instance = Room.databaseBuilder(context,ShopDatabase.class,"shop_database")
                    .fallbackToDestructiveMigration()
                    .allowMainThreadQueries()
                    .addCallback(initialCallback)
                    .build();
        }
        return instance;
    }

    private static RoomDatabase.Callback initialCallback = new RoomDatabase.Callback(){

        @Override
        public void onCreate(@NonNull SupportSQLiteDatabase db) {
            super.onCreate(db);

            new InitialAsyncTask(instance).execute();
        }
    };

    private static class InitialAsyncTask extends AsyncTask<Void, Void, Void> {

        private GroceryItemDao groceryItemDao;


        public InitialAsyncTask(ShopDatabase db) {
            this.groceryItemDao = db.groceryItemDao();
        }

        @Override
        protected Void doInBackground(Void... voids) {

            ArrayList<GroceryItem> allItems = new ArrayList<>();
            GroceryItem milk = new GroceryItem("Milk",
                    "Milk is a white, nutrient-rich liquid food produced in the mammary glands of mammals. It is the primary source of nutrition for infant mammals (including humans who are breastfed) before they are able to digest other types of food.[1] Early-lactation milk contains colostrum, which carries the mother's antibodies to its young and can reduce the risk of many diseases. It contains many other nutrients[2] including protein and lactose. Interspecies consumption of milk is not uncommon, particularly among humans, many of whom consume the milk of other mammals.",
                    "data:image/jpeg;base64,",
                    "drink",2.3,8);
            allItems.add(milk);

            GroceryItem iceCream = new GroceryItem("Ice Cream", "Delicious",
                    "https://encrypted-tbn0.gstatic.com/images?q=tbn%3AANd9GcTb9vkI6mGID6WrKL5WDfaefrXgpA3lLlNjZQ&usqp=CAU",
                    "food",
                    5.4,
                    10);
            allItems.add(iceCream);

            GroceryItem soda = new GroceryItem("Soda", " soft drink (see ยง Terminology for other names) is a drink that usually contains carbonated water (although some vitamin waters and lemonades are not carbonated), a sweetener, and a natural or artificial flavoring. ",
                    "data:image/jpeg;base64,",
                    "drink",
                    0.99, 15);
            allItems.add(soda);

            GroceryItem soap = new GroceryItem("Soap","A substance used with water for washing and cleaning, made of a compound of natural oils or fats with sodium hydroxide or another strong alkali, and typically having perfume and colouring added.",
                    "data:image/jpeg;base64,",
                    "Cleanser",
                    2.65,14);
            allItems.add(soap);

            GroceryItem juice = new GroceryItem("Juice","Juice is a drink made from the extraction or pressing of the natural liquid contained in fruit and vegetables. It can also refer to liquids that are flavored with concentrate or other biological food sources, such as meat or seafood, such as clam juice. Juice is commonly consumed as a beverage or used as an ingredient or flavoring in foods or other beverages, as for smoothies. ",
                    "data:image/jpeg;base64,",
                    "drink",3.45,4);
            allItems.add(juice);

            GroceryItem pistachio = new GroceryItem("Pistachio","The pistachio (/pษชหstษหสiหoส, -หstรฆ-/,[2] Pistacia vera), a member of the cashew family, is a small tree originating from Central Asia and the Middle East. The tree produces seeds that are widely consumed as food.",
                    "data:image/jpeg;base64,",
                    "Nuts",9.85,15);
            allItems.add(pistachio);

            GroceryItem walnut = new GroceryItem("Walnut","A walnut is the nut of any tree of the genus Juglans, particularly the Persian or English walnut, Juglans regia. A walnut is the edible seed of a drupe, and thus not a true botanical nut. It is commonly consumed as a nut.",
                    "data:image/jpeg;base64,",
                    "Nuts",5.6,4);
            allItems.add(walnut);

            for (GroceryItem g: allItems){
                groceryItemDao.insert(g);
            }
            return null;
        }
    }


}
